import React, { useEffect, useState } from "react";
import axios from "axios";

const GetBooks = () => {
  const [books, setBooks] = useState([]);
  const [rating, setRating] = useState({});

  // Fetch books from the backend
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get("http://localhost:8080/book/books");
        setBooks(response.data);
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };
    fetchBooks();
  }, []);

  // Handle rating selection
  const handleRating = (bookId, stars) => {
    const confirmRating = window.confirm(`Are you sure you want to rate this book ${stars} stars?`);
    if (confirmRating) {
      setRating((prev) => ({ ...prev, [bookId]: stars }));
      // Update the rating in the backend
      axios
        .put(`http://localhost:5000/books/rate/${bookId}`, { starRating: stars })
        .then(() => {
          alert("Rating submitted successfully!");
        })
        .catch((error) => {
          console.error("Error submitting rating:", error);
        });
    }
  };

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-3xl font-bold text-center mb-6">Books List</h1>
      <div className="grid grid-cols-1 gap-6">
        {books.map((book) => (
          <div
            key={book._id}
            className="flex items-center bg-white shadow-md rounded-lg p-4"
          >
            {/* Book Image */}
            <img
              src={`http://localhost:5000/${book.image}`}
              alt={book.title}
              className="w-24 h-32 object-cover rounded-md mr-6"
            />
            {/* Book Details */}
            <div className="flex-1">
              <h2 className="text-xl font-bold">{book.title}</h2>
              <p className="text-gray-700">by {book.author}</p>
              {/* Star Rating */}
              <div className="flex items-center mt-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <button
                    key={star}
                    onClick={() => handleRating(book._id, star)}
                    className={`text-xl ${
                      star <= (rating[book._id] || book.starRating || 0)
                        ? "text-yellow-500"
                        : "text-gray-400"
                    }`}
                  >
                    â˜…
                  </button>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GetBooks;
