import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const ReviewBook = () => {
  const { bookId } = useParams();
  const [review, setReview] = useState("");
  const [existingReview, setExistingReview] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Fetch the existing review for the book
  useEffect(() => {
    const fetchBookReview = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/book/books/${bookId}`);
        setExistingReview(response.data.reviewtext); // Assuming reviewtext is the field name in the book document
        setReview(response.data.reviewtext || ""); // Set the review text if available
      } catch (error) {
        console.error("Error fetching book review:", error);
      }
    };

    fetchBookReview();
  }, [bookId]);

  // Handle review submission
  const submitReview = async () => {
    setIsLoading(true); // Set loading state to true

    try {
      if (existingReview) {
        // Update review
        await axios.put(`http://localhost:8080/book/review/${bookId}`, { reviewtext: review });
        alert("Review updated successfully!");
      } else {
        // Submit new review
        await axios.put(`http://localhost:8080/book/review/${bookId}`, { reviewtext: review });
        alert("Review submitted successfully!");
      }

      // Redirect to the books list page after submission
      navigate("/Getbooks");
    } catch (error) {
      console.error("Error submitting review:", error);
      alert("Error submitting review, please try again.");
    } finally {
      setIsLoading(false); // Reset loading state
    }
  };

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-3xl font-bold text-center mb-6">{existingReview ? "Update Your Review" : "Review Book"}</h1>
      <div className="max-w-lg mx-auto">
        <textarea
          value={review}
          onChange={(e) => setReview(e.target.value)}
          placeholder="Write your review here..."
          className="w-full p-4 border rounded-lg"
          rows="5"
        />
        <button
          onClick={submitReview}
          className={`mt-4 px-4 py-2 ${isLoading ? "bg-gray-400 cursor-not-allowed" : "bg-green-500 hover:bg-green-600"} text-white rounded-md`}
          disabled={isLoading}
        >
          {isLoading ? "Submitting..." : existingReview ? "Update Review" : "Submit Review"}
        </button>
      </div>
    </div>
  );
};

export default ReviewBook;
